CREATE DEFINER=`root`@`localhost` PROCEDURE `GetTicketInfoForViewer`(in Idviewer INT)
BEGIN
    SELECT 
        E.IDperformance,
        E.Date1,
        E.timestart,
        P.name4 AS show1,
        P.director,
        P.productiondesigner,
        P.conductor,
        P.author,
        G.name7 AS genre,
        E.performancetype,
		R.rownumber AS rownumber,
        M.seatnumber AS seatnumber,
        T.IDcategory,
