    JOIN row1 R ON M.IDrow = R.IDrow
    JOIN performance E ON T.IDperformance = E.IDperformance
    JOIN show1 P ON E.IDshow = P.IDshow
    JOIN genre G ON P.IDgenre = G.IDgenre
    JOIN ticket_category C ON T.IDcategory = C.IDticketcategory
    WHERE T.IDviewer = idviewer;
END ;;
