CREATE DEFINER=`root`@`localhost` PROCEDURE `Афиша репетиций и выступлений`()
BEGIN
    SELECT P.IDperformance, P.Date1, P.Timestart,
	CASE P.performancetype 
             WHEN 'репетиция' THEN 'репетиция'
             ELSE 'выступление'
           END as type1,
           PL.name1 as area, PL.address, G.name5 as city
    FROM Performance as P
    INNER JOIN area as PL ON P.idarea = PL.idarea
    INNER JOIN city as G ON PL.idcity = G.idcity
    WHERE P.Date1 >= CURDATE();
END ;;
