CREATE DEFINER=`root`@`localhost` PROCEDURE `Информация о купленном билете`(in Idviewer INT)
BEGIN
    SELECT 
        E.IDperformance,
        E.Date1,
        E.timestart,
        P.name4 AS show1,
        P.director,
        P.productiondesigner,
        P.conductor,
        P.author,
        G.name7 AS genre,
        E.performancetype,
		R.rownumber AS rownumber,
        M.seatnumber AS seatnumber,
        T.IDcategory,
        C.name6 AS ticket_category,
        C.description1 AS description1,
        T.status1,
        T.price,
        `Рассчитать_стоимость_билета`( t.idticket,T.IDcategory) as discount_price
    FROM Tickets T
    JOIN seat M ON T.IDseat = M.IDseat
    JOIN row1 R ON M.IDrow = R.IDrow
    JOIN performance E ON T.IDperformance = E.IDperformance
    JOIN show1 P ON E.IDshow = P.IDshow
    JOIN genre G ON P.IDgenre = G.IDgenre
    JOIN ticket_category C ON T.IDcategory = C.IDticketcategory
    WHERE T.IDviewer = idviewer;
END ;;
