CREATE DEFINER=`root`@`localhost` PROCEDURE `Расписание репетиций и выступлений для актера`(IN IDemployee INT)
BEGIN
  SELECT 
    P.IDperformance AS "ID выступления",
    P.Date1 AS "Дата",
    P.Timestart AS "Время",
    P.performancetype AS "Тип выступления",
    Pl.name1 AS "Название площадки",
    Pl.Address AS "Адрес площадки",
    Ps.name4 AS "Постановка",
    A.Lastname AS "Фамилия артиста",
    A.Name2 AS "Имя артиста",
    A.Middlename AS "Отчество артиста"
  FROM performance AS P
  JOIN area AS Pl ON P.IDarea = Pl.IDarea
  JOIN show1 AS Ps ON P.IDshow = Ps.idshow
  JOIN performingartists AS PA ON P.IDperformance = PA.IDperformance
  JOIN artists AS A ON PA.IDemployee = A.IDemployee
  WHERE A.IDemployee = IDemployee
  ORDER BY P.Date1, P.Timestart;
END ;;
