CREATE DEFINER=`root`@`localhost` PROCEDURE `Свободные места`(idperformance BIGINT)
BEGIN
    SELECT seat.IDseat, row1.IDrow, tickets.price
    FROM seat
    INNER JOIN row1 ON seat.IDrow = row1.IDrow
    INNER JOIN tickets ON seat.IDseat = tickets.IDseat
    WHERE tickets.IDperformance = idperformance
    AND (tickets.status1 = 'свободно');
END ;;
